    public void addToHeatMapData(HashMap data){
        data.values().forEach(value->{
            JSONArray thisArray = (JSONArray) value;
            for (int j = 0 ; j < thisArray.length(); j++){
                try {
                    JSONObject obj = thisArray.getJSONObject(j);
                    JSONObject loc = obj.getJSONObject("location");
                    heatMapData.add(new LatLng(Double.parseDouble(loc.getString("latitude")),Double.parseDouble(loc.getString("longitude"))));
                } catch(JSONException e) {
                    Log.d("error",e.toString());
                }
            }
        });
        Log.d("heat map data length",heatMapData.size()+"");
    }
    public void drawGraphs(String area) {
        final TextView textView = findViewById(R.id.textView2);
        Spinner spinner2 = findViewById(R.id.month_spinner);
        spinner2.setEnabled(false);
        spinner2.setClickable(false);


        if(!masterData.containsKey(area)) {
            textView.setText("Fetching Data...");
            getAllCrime(area).then(result->{
                textView.setText("Data Fetched!");
                masterData.put(area, (HashMap) result);
                drawCrimeByCategoryGraph((HashMap) result, currentMonth);
                drawCrimeByMonthGraph((HashMap) result);
                drawHeatMap();
                spinner2.setEnabled(true);
                spinner2.setClickable(true);
                return 1;
            });

        } else {
            spinner2.setEnabled(true);
            spinner2.setClickable(true);
            HashMap result = masterData.get(area);
            drawCrimeByCategoryGraph(result, currentMonth);
            drawCrimeByMonthGraph(result);
            drawHeatMap();
        }
    }
    private void drawCrimeByCategoryGraph(HashMap<String,JSONArray> result, String Month){
        HashMap<String, Integer> data = new HashMap<>();
        JSONArray arr = result.get(Month);
        for (int j = 0; j < arr.length(); j++){
            try {
                JSONObject tab = arr.getJSONObject(j);
                String category = tab.getString("category");
                if(data.containsKey(category)){
                    data.put(category,data.get(category) + 1);
                } else {
                    data.put(category,1);
                }
            } catch(JSONException e){
                Log.d("error",e.toString());
            }
        }
        //Log.d("called",data.toString());
        BarChart barChart = findViewById(R.id.barchart);
        List<BarEntry> entries = new ArrayList<>();
        int val = 0;
        List<Integer> colors = new ArrayList<>();
        List<String> categories = new ArrayList<>();
        int keyCount = 1;
        HashMap<String ,Integer> trimedData = new HashMap<>();
        for(Map.Entry<String,Integer> entry: data.entrySet()){
            if(keyCount < 10){
                trimedData.put(entry.getKey(),entry.getValue());
            }
            keyCount++;
        }
        Map<String,Integer>  sortedCategories =  trimedData.entrySet().
                stream().
                sorted(Map.Entry.comparingByValue(Comparator.naturalOrder())).
                collect(Collectors.toMap(Map.Entry::getKey, Map.Entry::getValue, (e1, e2) -> e1, LinkedHashMap::new));

        Log.d("categories",sortedCategories.keySet().size() + "");
        Log.d("categories values",sortedCategories.values().size()+"");
        for (String key: sortedCategories.keySet()){
            categories.add(key);
        }
        Log.d("categories",categories.toString());
        ValueFormatter formatter = new ValueFormatter() {
            @Override
            public String getAxisLabel(float value, AxisBase axis) {
                return categories.get((int) value);
            }
        };

        for(Integer value: sortedCategories.values()){
            Random rnd = new Random();
            int color = Color.argb(255, rnd.nextInt(256), rnd.nextInt(256), rnd.nextInt(256));
            colors.add(color);
            entries.add(new BarEntry(val, value));
            val++;
        }
        BarDataSet set = new BarDataSet(entries,"Crime By Category");
        set.setColors(colors);
        BarData barData = new BarData(set);
        //barData.setBarWidth(0.5f);
        barChart.setData(barData);
        barChart.setFitBars(true);
        Description description = barChart.getDescription();
        description.setEnabled(false);
        XAxis xAxis = barChart.getXAxis();
        xAxis.setGranularity(1f); // minimum axis-step (interval) is 1
        xAxis.setLabelCount(10,false);
        xAxis.setValueFormatter(formatter);
        xAxis.setPosition(XAxis.XAxisPosition.TOP_INSIDE);
        xAxis.setDrawAxisLine(true);
        xAxis.setDrawGridLines(false);
        barChart.invalidate();

    }
