    public Promise getCrime(String month, String force){
        Log.d("called",month+" "+force);
        Promise p = new Promise();
        new Thread(()->{
            String boundary = getBoundaries(force);
            String url = "https://data.police.uk/api/crimes-street/all-crime";
            VolleyMultipartRequest multipartRequest;
            multipartRequest = new VolleyMultipartRequest(Request.Method.POST, url, new Response.Listener<NetworkResponse>() {
                @Override
                public void onResponse(NetworkResponse response) {
                    Log.d("got response",month+" "+force);

                    //String resultResponse =
                    try {
                        JSONArray jsonArray = new JSONArray(new String(response.data));
                        JSONArray thisArr = new JSONArray();
                        for (int i = 0; i < jsonArray.length(); i++){
                            JSONObject thisObj = new JSONObject();
                            JSONObject obj = jsonArray.getJSONObject(i);
                            //JSONObject loc = obj.getJSONObject("location");
                            heatMapData.add(new LatLng(obj.getJSONObject("location").getDouble("latitude"),obj.getJSONObject("location").getDouble("longitude")));
                            //thisObj.put("location",obj.get("location"));
                            thisObj.put("category",obj.get("category"));
                            thisArr.put(thisObj);
                        }
                        JSONObject obj = new JSONObject();
                        obj.put("month",month);
                        obj.put("data",thisArr);
                        Log.d("month",month);
                        Log.d("result",thisArr.length()+"");
                        p.resolve(obj);
                    } catch(JSONException e) {
                        p.reject(e.getMessage());
                    }
                }
            }, new Response.ErrorListener() {
                @Override
                public void onErrorResponse(VolleyError error) {
                    Log.d("error",error.toString());
                    Log.d("rejecting",month+" "+force);
                    getCrime(month,force).then(obj -> {
                       p.resolve(obj);
                       return 1;
                    });
                    //p.reject(error.getMessage());
                }
            }) {
                @Override
                protected Map<String, String> getParams() {
                    Map<String, String> params = new HashMap<>();
                    params.put("poly", ((String) boundary));
                    params.put("date", month);
                    return params;
                }
            };
            requestQueue.add(multipartRequest);
        }).run();
        return p;
    }
    public String getBoundaries(String force){
        return boundaries.get(force);
    }
    public void onTrimMemory(int level) {

        // Determine which lifecycle or system event was raised.
        switch (level) {

            case ComponentCallbacks2.TRIM_MEMORY_RUNNING_MODERATE:
            case ComponentCallbacks2.TRIM_MEMORY_RUNNING_LOW:
            case ComponentCallbacks2.TRIM_MEMORY_RUNNING_CRITICAL:
                Log.d("memory level",level+"");
                /*
                   Release any memory that your app doesn't need to run.

                   The device is running low on memory while the app is running.
                   The event raised indicates the severity of the memory-related event.
                   If the event is TRIM_MEMORY_RUNNING_CRITICAL, then the system will
                   begin killing background processes.
                */

                break;
            default:
                Log.d("memory level",level+"");

                /*
                  Release any non-critical data structures.

                  The app received an unrecognized memory level value
                  from the system. Treat this as a generic low-memory message.
                */
                break;
        }
    }
